select count(*) FROM
EXPENSESRECEIPTS 


		and roleid = #{roleid}


		and playeracc like CONCAT(CONCAT('%', #{playeracc}), '%')


		and buyroleName like CONCAT(CONCAT('%', #{buyroleName}), '%')


		and type = #{type}

select * FROM EXPENSESRECEIPTS
and roleid = #{roleid}
and playeracc like CONCAT(CONCAT('%', #{playeracc}), '%')
and buyroleName like CONCAT(CONCAT('%', #{buyroleName}), '%')
and type = #{type}
order by paytime desc


select erid  from expensesreceipts 
where 
SID = #{sid,jdbcType=DECIMAL}
and 
PAYTIME >= #{start,jdbcType=VARCHAR}
and
PAYTIME <= #{end,jdbcType=VARCHAR}
