mapper.update:
UPDATE mount SET
mountname = #{ mountname }, 
spri = #{ spri }, power = #{ power },
bone = #{ bone },
live = #{ live }, exp = #{
exp }, sid = #{
sid },
othrersid = #{ othrersid }, mountlvl = #{
mountlvl },useNumber = #{ useNumber },useNumbers = #{ useNumbers },Proficiency=#{Proficiency},
sid3 = #{sid3 }
WHERE mid =
#{ mid }

mapper.select:
SELECT * FROM 
(
    SELECT A.* , ROWNUM RN  from 
    ( 
    	SELECT MOUNTNAME,MOUNTLVL,LIVE,SPRI,BONE,EXP,USENUMBER,USENUMBERS,PROFICIENCY,ROLENAME,
    	SID,OTHRERSID,SID3 FROM MOUNT_ROLE_USERTABLE 


    			AND MOUNTNAME LIKE CONCAT( '%' , CONCAT( #{mru.mountname} , '%' ))


    			AND ROLENAME LIKE CONCAT( '%' , CONCAT( #{mru.rolename} , '%' ))


    	${mru.orderBy} 
    ) A  
    WHERE ROWNUM < #{mru.end} 
) 
WHERE RN >= #{mru.start}

