mapper.select:
SELECT * FROM 
(
    SELECT A.* , ROWNUM RN  from 
    (
    	SELECT SUMMONINGID,SUMMONINGNAME,RESISTANCE,SKILL,SKILLDATA,FOURATTRIBUTES,LYK,
    	ZQK,ALCHEMYNUM,GROWUPDANNUM,ROLENAME,BASISHP,BASISMP,BASISAP,BASISSP,GROWLEVEL,
    	TURNROUNT,GRADE 
    	FROM ROLESUMMONING_ROLE_USERTABLE 


    			AND SUMMONINGID = #{rru.summoningid} 


    			AND ROLENAME LIKE CONCAT( '%' , CONCAT( #{rru.rolename} , '%'))  


    			AND SKILL LIKE CONCAT( '%' , CONCAT( #{rru.skill} , '%'))  


    	${rru.orderBy} 
    ) A  
    WHERE ROWNUM < #{rru.end} 
) 
WHERE RN >= #{rru.start}

SELECT COUNT(*) FROM ( SELECT SUMMONINGID,SUMMONINGNAME,RESISTANCE,SKILL,SKILLDATA,FOURATTRIBUTES,LYK, ZQK,ALCHEMYNUM,GROWUPDANNUM,ROLENAME,BASISHP,BASISMP,BASISAP,BASISSP,GROWLEVEL, TURNROUNT,GRADE FROM ROLESUMMONING_ROLE_USERTABLE
AND SUMMONINGID = #{rru.summoningid}
AND ROLENAME LIKE CONCAT( '%' , CONCAT( #{rru.rolename} , '%'))
AND SKILL LIKE CONCAT( '%' , CONCAT( #{rru.skill} , '%'))
)

